import React, { useState, useEffect } from 'react';
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(8);
  const [editingProduct, setEditingProduct] = useState(null);

  // Simula o carregamento de produtos de uma API
  useEffect(() => {
    const fetchProducts = async () => {
      // Substitua isso por uma chamada real à API
      const response = await fetch('https://api.example.com/products');
      const data = await response.json();
      setProducts(data);
    };

    fetchProducts();
  }, []);

  // Filtra produtos baseado no termo de busca
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Ordena produtos
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    if (sortBy === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortBy === 'price') {
      return a.price - b.price;
    } else if (sortBy === 'quantity') {
      return a.quantity - b.quantity;
    }
    return 0;
  });

  // Calcula produtos para a página atual
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = sortedProducts.slice(indexOfFirstProduct, indexOfLastProduct);

  // Muda de página
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Manipula a adição de um novo produto
  const handleAddProduct = () => {
    const newProduct = {
      id: Date.now(),
      name: 'Novo Produto',
      price: 0,
      quantity: 0
    };
    setProducts([...products, newProduct]);
    setEditingProduct(newProduct);
  };

  // Manipula a edição de um produto
  const handleEditProduct = (product) => {
    setEditingProduct(product);
  };

  // Salva as alterações de um produto
  const handleSaveProduct = (editedProduct) => {
    setProducts(products.map(p => p.id === editedProduct.id ? editedProduct : p));
    setEditingProduct(null);
  };

  // Manipula a exclusão de um produto
  const handleDeleteProduct = (productId) => {
    setProducts(products.filter(p => p.id !== productId));
  };

  return (
    <div className="container">
      <header className="header">
        <h1>Lista de Produtos</h1>
        <button className="btn btn-primary" onClick={handleAddProduct}>Adicionar Produto</button>
      </header>
      
      <div className="controls">
        <input
          type="text"
          placeholder="Pesquisar por nome"
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          className="sort-select"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="">Ordenar por</option>
          <option value="name">Nome</option>
          <option value="price">Preço</option>
          <option value="quantity">Quantidade</option>
        </select>
      </div>

      <div className="product-grid">
        {currentProducts.map(product => (
          <div key={product.id} className="product-card">
            {editingProduct && editingProduct.id === product.id ? (
              <div className="edit-form">
                <input
                  type="text"
                  value={editingProduct.name}
                  onChange={(e) => setEditingProduct({...editingProduct, name: e.target.value})}
                />
                <input
                  type="number"
                  value={editingProduct.price}
                  onChange={(e) => setEditingProduct({...editingProduct, price: parseFloat(e.target.value)})}
                />
                <input
                  type="number"
                  value={editingProduct.quantity}
                  onChange={(e) => setEditingProduct({...editingProduct, quantity: parseInt(e.target.value)})}
                />
                <button onClick={() => handleSaveProduct(editingProduct)}>Salvar</button>
              </div>
            ) : (
              <>
                <h3 className="product-name">{product.name}</h3>
                <p className="product-price">R$ {product.price.toFixed(2)}</p>
                <p className="product-quantity">Quantidade: {product.quantity}</p>
                <div className="product-actions">
                  <button className="btn btn-edit" onClick={() => handleEditProduct(product)}>Editar</button>
                  <button className="btn btn-delete" onClick={() => handleDeleteProduct(product.id)}>Excluir</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>

      <div className="pagination">
        {Array.from({ length: Math.ceil(sortedProducts.length / productsPerPage) }).map((_, index) => (
          <button key={index} onClick={() => paginate(index + 1)} className="btn btn-page">
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
