import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductForm from './ProductForm'; // Importe o formulário de produto

function ProductList() {
  const [products, setProducts] = useState([]);  // Inicializa a lista de produtos
  const [error, setError] = useState(null);  // Estado para armazenar erros
  const [productToEdit, setProductToEdit] = useState(null);  // Estado para o produto a ser editado

  // Função para buscar a lista de produtos
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');  // Pega os produtos da API
        setProducts(response.data.data);  // Define os produtos
      } catch (error) {
        console.error('Erro ao buscar os produtos:', error);
        setError('Erro ao carregar os produtos');
      }
    };

    fetchProducts();
  }, []);

  // Função chamada após a criação ou edição de um produto
  const handleFormSubmit = () => {
    // Recarregar a lista de produtos após adicionar ou editar
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data.data);
        setProductToEdit(null);  // Limpar o produto de edição
      } catch (error) {
        console.error('Erro ao buscar os produtos:', error);
      }
    };
    fetchProducts();
  };

  // Função para excluir um produto
  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/products/${id}`);
      setProducts(products.filter(product => product.id !== id));  // Remove o produto excluído da lista
    } catch (error) {
      console.error('Erro ao excluir o produto:', error);
      setError('Erro ao excluir o produto');
    }
  };

  // Função para definir o produto a ser editado
  const handleEditProduct = (product) => {
    setProductToEdit(product);  // Define o produto que será editado
  };

  // Verifique se `products` é realmente um array antes de usar `.map`
  if (!Array.isArray(products)) {
    return <p>Erro: dados de produtos inválidos</p>;
  }

  // Renderizar a lista de produtos
  return (
    <div>
      <h2>Lista de Produtos</h2>
      {error ? (
        <p>{error}</p>  // Exibe mensagem de erro, se houver
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              {product.name} - R$ {product.price} (Quantidade: {product.quantity})
              <button onClick={() => handleEditProduct(product)}>Editar</button>  {/* Botão para editar */}
              <button onClick={() => deleteProduct(product.id)}>Excluir</button>  {/* Botão para excluir */}
            </li>
          ))}
        </ul>
      )}

      <h2>{productToEdit ? 'Editar Produto' : 'Adicionar Novo Produto'}</h2>
      {/* Renderiza o formulário para criar ou editar produto */}
      <ProductForm
        productToEdit={productToEdit}
        onFormSubmit={handleFormSubmit}
      />
    </div>
  );
}

export default ProductList;
