import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductForm from './ProductForm'; // Importe o formulário de produto

function ProductList() {
  const [products, setProducts] = useState([]);  // Inicializa a lista de produtos
  const [filteredProducts, setFilteredProducts] = useState([]);  // Lista filtrada de produtos
  const [searchTerm, setSearchTerm] = useState('');  // Estado para o termo de pesquisa
  const [error, setError] = useState(null);  // Estado para armazenar erros
  const [productToEdit, setProductToEdit] = useState(null);  // Estado para o produto a ser editado

  // Função para buscar a lista de produtos
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');  // Pega os produtos da API
        setProducts(response.data.data);  // Define os produtos
        setFilteredProducts(response.data.data);  // Inicialmente, a lista filtrada contém todos os produtos
      } catch (error) {
        console.error('Erro ao buscar os produtos:', error);
        setError('Erro ao carregar os produtos');
      }
    };

    fetchProducts();
  }, []);

  // Função para excluir um produto
  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/products/${id}`);
      setProducts(products.filter(product => product.id !== id));  // Remove o produto excluído da lista
      setFilteredProducts(filteredProducts.filter(product => product.id !== id));  // Atualiza a lista filtrada
    } catch (error) {
      console.error('Erro ao excluir o produto:', error);
      setError('Erro ao excluir o produto');
    }
  };

  // Função para definir o produto a ser editado
  const handleEditProduct = (product) => {
    setProductToEdit(product);  // Define o produto que será editado
  };

  // Função para lidar com a pesquisa de produtos
  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();  // Termo de busca em letras minúsculas
    setSearchTerm(term);

    // Filtra os produtos com base no termo de busca (por nome ou preço)
    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(term) || product.price.toString().includes(term)
    );
    setFilteredProducts(filtered);  // Atualiza a lista filtrada
  };

  return (
    <div>
      <h2>{productToEdit ? 'Editar Produto' : 'Adicionar Novo Produto'}</h2>

      <ProductForm productToEdit={productToEdit} onFormSubmit={handleEditProduct} />

      <input
        type="text"
        placeholder="Pesquisar Produto"
        value={searchTerm}
        onChange={handleSearch}
        style={{ margin: '20px 0', padding: '10px', width: '100%' }}
      />

      <h2>Lista de Produtos</h2>
      {error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {filteredProducts.map((product) => (
            <li key={product.id}>
              <div className="product-content">
                {product.name} - R$ {product.price} (Quantidade: {product.quantity})
              </div>
              <div className="button-container">
                <button onClick={() => handleEditProduct(product)}>Editar</button>
                <button onClick={() => deleteProduct(product.id)}>Excluir</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ProductList;
