import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductForm from './ProductForm';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [error, setError] = useState(null);
  const [productToEdit, setProductToEdit] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [orderBy, setOrderBy] = useState('');
  const limit = 10;

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products`, {
          params: {
            page: currentPage,
            limit: limit,
            name: searchTerm,
            orderBy: orderBy,
          },
        });

        setProducts(response.data.data);
        setTotalPages(response.data.totalPages);
        setError(null);
      } catch (error) {
        console.error('Erro ao buscar os produtos:', error);
        setError('Erro ao carregar os produtos');
      }
    };

    fetchProducts();
  }, [currentPage, searchTerm, orderBy]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1);
  };

  const handleOrderChange = (e) => {
    setOrderBy(e.target.value);
    setCurrentPage(1);
  };

  return (
    <div>
      <h2>{productToEdit ? 'Editar Produto' : 'Adicionar Novo Produto'}</h2>

      <ProductForm productToEdit={productToEdit} onFormSubmit={() => setCurrentPage(1)} />

      <div style={{ margin: '20px 0' }}>
        <input
          type="text"
          placeholder="Pesquisar por nome"
          value={searchTerm}
          onChange={handleSearch}
          style={{ padding: '10px', marginRight: '10px' }}
        />
        <select onChange={handleOrderChange} value={orderBy} style={{ padding: '10px' }}>
          <option value="">Ordenar por</option>
          <option value="quantity-asc">Quantidade (Crescente)</option>
        </select>
      </div>

      <h2>Lista de Produtos</h2>
      {error ? (
        <p>{error}</p>
      ) : (
        <>
          <ul>
            {products.map((product) => (
              <li key={product.id}>
                <div className="product-content">
                  {product.name} - R$ {product.price} (Quantidade: {product.quantity})
                </div>
                <div className="button-container">
                  <button onClick={() => setProductToEdit(product)}>Editar</button>
                  <button onClick={() => deleteProduct(product.id)}>Excluir</button>
                </div>
              </li>
            ))}
          </ul>

          <div className="pagination">
            {Array.from({ length: totalPages }, (_, index) => (
              <button
                key={index}
                className={currentPage === index + 1 ? 'active' : ''}
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default ProductList;
