import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductList() {
  const [products, setProducts] = useState([]);  // Inicializa como array vazio
  const [error, setError] = useState(null);  // Estado para armazenar erros

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setProducts(response.data.data);  // Definir produtos da resposta
      } catch (error) {
        console.error('Erro ao buscar os produtos:', error);
        setError('Erro ao carregar os produtos');
      }
    };

    fetchProducts();
  }, []);

  // Função para excluir um produto
  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/products/${id}`);
      setProducts(products.filter(product => product.id !== id));  // Remove o produto excluído da lista
    } catch (error) {
      console.error('Erro ao excluir o produto:', error);
      setError('Erro ao excluir o produto');
    }
  };

  // Função de exemplo para editar um produto (você pode expandir isso com um formulário)
  const editProduct = (id) => {
    const newName = prompt('Digite o novo nome do produto:');  // Exemplo simples de edição
    const newPrice = prompt('Digite o novo preço do produto:');
    
    if (newName && newPrice) {
      axios.put(`http://localhost:5000/products/${id}`, { name: newName, price: parseFloat(newPrice) })
        .then(response => {
          setProducts(products.map(product => 
            product.id === id ? { ...product, name: newName, price: parseFloat(newPrice) } : product
          ));
        })
        .catch(error => {
          console.error('Erro ao editar o produto:', error);
          setError('Erro ao editar o produto');
        });
    }
  };

  // Verifique se `products` é realmente um array antes de usar `.map`
  if (!Array.isArray(products)) {
    return <p>Erro: dados de produtos inválidos</p>;
  }

  return (
    <div>
      <h1>Lista de Produtos</h1>
      {error ? (
        <p>{error}</p>  // Exibir mensagem de erro, se houver
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              {product.name} - R$ {product.price}
              <button onClick={() => editProduct(product.id)}>Editar</button>  {/* Botão para editar */}
              <button onClick={() => deleteProduct(product.id)}>Excluir</button>  {/* Botão para excluir */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ProductList;
